sp_widget.client_script:
api.controller=function() 
{
	/* widget controller */
	var c = this;

	c.updateNote = function(updateType) {
		c.server.get({
			action: 'updateNote',
			noteID: c.data.noteID,
			noteBody: c.data.note,
			noteTitle: c.data.title
		}).then(function(r) {
			$rootScope.$emit('updateNote', {
				noteTitle: c.data.title,
				noteBody: c.data.note,
				noteID: c.data.noteID
			})
		});
	}

	$rootScope.$on('selectNote', function(event, data) {
		c.server.get(
			{
				action: 'getNote',
				noteID: $rootScope.noteID
			}
		).then(function(r) {
			c.data.title = r.data.note.title;
			c.data.note = r.data.note.note;
			c.data.noteID = r.data.note.sys_id;
		})
	});
};

sp_widget.script:
(function() {
	/* populate the 'data' object */

	if (input && input.noteID)
	{
		var grNote = new GlideRecord('x_snc_createnotes_note');
		if (grNote.get(input.noteID))
		{
			if (input.action == 'getNote')
			{
				data.note = {};
				$sp.getRecordValues(data.note, grNote, 'title, note, sys_id');
			}
			else if (input.action == 'updateNote') 
			{
				grNote.title = input.noteTitle;
				grNote.note = input.noteBody;
				grNote.update();
			}
		} 
	}
})();
