sp_widget.client_script:
api.controller=function() 
{
  /* widget controller */
  var c = this;
	c.selectItem = function(idx)
	{
		const id = c.data.notes[idx].sys_id;
		$rootScope.noteID = id;
		$rootScope.$emit('selectNote', id);
	};

	$rootScope.$on('updateNote', function(event, data) {
		const iIndex = c.data.notes.findIndex((oNote) => oNote.sys_id == data.noteID);
		c.data.notes[iIndex].title = data.noteTitle;
		c.data.notes[iIndex].note = data.noteBody;
	});
};

sp_widget.script:
(function() 
 {
	// create an array to populate with notes
	data.notes = [];
	const grNote = new GlideRecord('x_snc_createnotes_note');
	grNote.addQuery('user', gs.getUser().getID());
	grNote.orderByDesc('sys_created_on');
	grNote.query();

	while(grNote.next())
	{
		var oNote = {};

		// use service portal helper method to get some display values
		$sp.getRecordDisplayValues(oNote, grNote, 'number,title,sys_id');

		// get the first 20 characters of the description
		oNote.note = grNote.getValue('note').slice(0, 20);

		// push the populated object into the array
		data.notes.push(oNote);
	}
})();
